export default function App() {
  const [formData, setFormData] = React.useState({
    firstName: "",
    lastName: "",
    email: "",
    Comments: "",
    iSFriendly: true,
    employment: "",
  });
  console.log(formData);
  function handleChange(event) {
    const { name, value, type, checked } = event.target;
    setFormData((prevFormData) => {
      return {
        ...prevFormData,
        [name]: type === "checkbox" ? checked : value,
      };
    });
  }

  return (
    <form>
      <input
        name="firstName"
        type="text"
        placeholder="First Name"
        onChange={handleChange}
        value={formData.firstName}
      />
      <input
        name="lastName"
        type="text"
        placeholder="Last Name"
        onChange={handleChange}
        value={formData.lastName}
      ></input>
      <input
        name="email"
        type="email"
        placeholder="email"
        onChange={handleChange}
        value={formData.email}
      ></input>

      <textarea
        value={formData.Comments}
        placeholder="comments"
        onChange={handleChange}
        name="comments"
      />
      <br></br>
      <input
        type="checkbox"
        id="isFriendly"
        checked={formData.iSFriendly}
        onChange={handleChange}
        name="iSFriendly"
      ></input>
      <label htmlFor="isFriendly">react</label>
      <br></br>
      <br></br>
      <fieldset>

        <legend>Current Employment status</legend>
        <input type="radio" id="unemployment" name="employment" />
        <label htmlFor="unemployemnt">Unemployed</label>
        <br></br>
        <input type="radio" id="part-time" name="employment"></input>
        <label htmlFor="part-time">Part-time</label>
        <br></br>
        <input type="radio" id="full-time" name="employment" />
        <label htmlFor="full-time">Full-time</label>
        <br></br>
      </fieldset>
    </form>
  );
}
