import React from "react";
import Die from "./die";
import "./App.css";
export default function App() {
  const [dice, setDice] = React.useState(allNewDice());

  function allNewDice() {
    const newDice = [];
    for (let i = 0; i < 10; i++) {
      newDice.push(Math.ceil(Math.random() * 6));
    }
    return newDice;
  }

  const diceElements = dice.map((die) => <Die value={die}></Die>);
  return (
    <main>
      <div className="dice-container">
       {diceElements}
      </div>
      <button>Roll</button>
    </main>
  );
}
