import React from "react";
import memesData from "./memesdata";

export default function Meme() {
  const [memeImage, setMemeImage] = React.useState(
    "http://i.imgflip.com/1bij.jpg"
  );

  const [meme, setMeme] = React.useState({
    topText: "",
    bottomText: "",
    // randomImage: "http://i.imgflip.com/1bij.jpg",
    randomImage:
      "https://www.google.com/search?q=nico+robin&tbm=isch&ved=2ahUKEwjX_5vy5YD7AhWojNgFHezXA-sQ2-cCegQIABAA&oq=nico+robin&gs_lcp=CgNpbWcQAzIECCMQJzIECCMQJzIECAAQQzIECAAQQzIECAAQQzIKCAAQsQMQgwEQQzIECAAQQzIECAAQQzIFCAAQgAQyBQgAEIAEOggIABCxAxCDAToICAAQgAQQsQM6CwgAEIAEELEDEIMBUNYQWLtMYJZQaAFwAHgAgAGJAogB7BySAQYwLjIyLjGYAQCgAQGqAQtnd3Mtd2l6LWltZ8ABAQ&sclient=img&ei=Q61aY5fSDaiZ4t4P7K-P2A4&rlz=1C1ONGR_enIN1020IN1020#imgrc=d4uMwPm7AjABwM&imgdii=bWeRdtPuGao3VM",
  });

  const [allMemeImages, setAllMemeImages] = React.useState(memesData);

  function getMemeImage() {
    const memesArray = allMemeImages.data.memes;

    const randomNumber = Math.floor(Math.random() * memesArray.length);
    const url = memesArray[randomNumber].url;
    setMeme((prevMeme) => ({ ...prevMeme, randomImage: url }));
  }

  return (
    <main>
      <div className="form">
        <input className="form-input" placeholder="top" type="text" />
        <input className="form-input" placeholder="bottom" type="text" />
        <button onClick={getMemeImage} className="form-button">
          Get a new meme image
        </button>
      </div>
      <img src={meme.randomImage} className="meme-img" alt="Error"></img>
    </main>
  );
}
